mapper.select:
SELECT t_knowledge_point.KNOWLEDGE_POINT_ID, t_knowledge_point.POINT_TITLE, t_knowledge_point.PID, t_knowledge_point.LEAF, t_knowledge_point.ICON, t_knowledge_point.CREATE_USER, t_knowledge_point.CREATE_DATE, t_knowledge_point.UPDATE_DATE, t_knowledge_point.DESCRIPTION, t_knowledge_point.STATUS, e.NAME, t_knowledge_point.SORT FROM t_knowledge_point, t_employee as e WHERE e.EMPLOYEE_ID = t_knowledge_point.CREATE_USER AND t_knowledge_point.PID = #{pid}
AND t_knowledge_point.POINT_TITLE like CONCAT('%',#{pointTitle},'%')
AND t_knowledge_point.STATUS = #{status}
ORDER BY t_knowledge_point.SORT
LIMIT #{currentPage},10;

SELECT count(1) FROM t_knowledge_point WHERE t_knowledge_point.PID = #{pid}
AND t_knowledge_point.POINT_TITLE like CONCAT('%',#{pointTitle},'%')
AND t_knowledge_point.STATUS = #{status}

SELECT t_knowledge_point.KNOWLEDGE_POINT_ID, t_knowledge_point.POINT_TITLE, t_knowledge_point.PID, t_knowledge_point.LEAF, t_knowledge_point.ICON, t_knowledge_point.CREATE_USER, t_knowledge_point.CREATE_DATE, t_knowledge_point.UPDATE_DATE, t_knowledge_point.DESCRIPTION, t_knowledge_point.STATUS, t_knowledge_point.SORT FROM t_knowledge_point WHERE t_knowledge_point.PID = #{pid}
AND t_knowledge_point.STATUS = #{status}
ORDER BY t_knowledge_point.SORT
