mapper.select:
select b.ER_NUMBER,b.HR_NUMBER,b.`NAME`,b.ENAME ,c.`NAME` as NAME1,d.`NAME` as NAME2, group_concat(distinct a.course_name) as NAME3 from t_employee b left join (select a.employee_id, a.training_id, b.course_name from t_personal_training a, t_training b where b.training_id = a.training_id) a on a.employee_id=b.employee_id left join t_bu c on c.bu_id=b.bu_id left join t_project d on d.project_id=b.project_id WHERE 1 = 1
AND c.BU_ID = #{buId}
AND d.PROJECT_ID = #{projectId}
AND a.TRAINING_ID = #{trainingId}
group by b.employee_id LIMIT #{currentPage},10;

select b.ER_NUMBER,b.HR_NUMBER,b.`NAME`,b.ENAME ,c.`NAME` as NAME1,d.`NAME` as NAME2, group_concat(distinct a.course_name) as NAME3 from t_employee b left join (select a.employee_id, a.training_id, b.course_name from t_personal_training a, t_training b where b.training_id = a.training_id and a.status=1) a on a.employee_id=b.employee_id left join t_bu c on c.bu_id=b.bu_id left join t_project d on d.project_id=b.project_id WHERE 1 = 1
AND c.BU_ID = #{buId}
AND d.PROJECT_ID = #{projectId}
AND a.TRAINING_ID = #{trainingId}
group by b.employee_id LIMIT #{currentPage},10;
